fn conn_event(&mut self, poll: &mut mio::Poll, event: &mio::Event) {
    let token = event.token();

    if self.connections.contains_key(&token) {
        self.connections
            .get_mut(&token)
            .unwrap()
            .ready(poll, event);

        if self.connections[&token].is_closed() {
            self.connections.remove(&token);
        }
    }
}

